import React from "react";
import ReactDOM from "react-dom";
//import SeasonDisplay from "./SeasonDisplay";

class App extends React.Component{
  constructor(props) {
    super(props);
    ///////////// states
    this.state = {latitude: null, errorMessage: '', isLoaded: null};
    /////////////

    navigator.geolocation.getCurrentPosition(
        success => {
          return this.setState({latitude: success.coords.latitude, isLoaded: true});
        },
        error => {
          return this.setState({errorMessage: error.message, isLoaded: false});
        }
    );
  }

  render(){
   /* if(this.state.errorMessage && !this.state.latitude){
      return <div>Error: {this.state.errorMessage}</div>
    }
    if(!this.state.errorMessage && this.state.latitude){
      return <div>Latitude: {this.state.latitude}</div>
    }
      return <div>loading...</div>
  } */

    let isLoaded = this.state.isLoaded;
    return(
        <div>{isLoaded ? "Latitude: " + this.state.latitude : "Error: " + this.state.errorMessage}</div>

    );
  }
}

ReactDOM.render(<App />, document.querySelector("#root"));
