import React from "react";
import ReactDOM from "react-dom";
import SeasonDisplay from "./SeasonDisplay";

class App extends React.Component{
  constructor(props) {
    super(props);
    this.state = {latitude: 0, longitude: 0};
  }

  render = () => {

    const success = (position) =>{
      const location = position.coords;
      this.setState({latitude: location.latitude});
      this.setState({longitude: location.longitude})

    }
    const err = () =>{
      const errMessage = "Nie można określić lokalizacji"
      this.setState({latitude: errMessage});
      this.setState({longitude: errMessage})
    }
    navigator.geolocation.getCurrentPosition(success, err)
    return(
        <div>
          <p>Latitude: {this.state.latitude}</p>
          <p>Longitude: {this.state.longitude}</p>
        </div>
    );
  };
}

ReactDOM.render(<App />, document.querySelector("#root"));
